import { useState } from 'react';
import {
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from '@mui/material';
import axios from 'axios';

const endpointMapping = {
  'Notion': 'notion',
  'Airtable': 'airtable',
  'HubSpot': 'hubspot'
};

export const DataForm = ({ integrationType, credentials }) => {
  const [loadedData, setLoadedData] = useState(null);
  const endpoint = endpointMapping[integrationType];

  const handleLoad = async () => {
    try {
      const formData = new FormData();
      formData.append('credentials', JSON.stringify(credentials));
      const response = await axios.post(`http://localhost:8000/integrations/${endpoint}/load`, formData);
      setLoadedData(response.data);
    } catch (e) {
      alert(e?.response?.data?.detail || 'Error loading data');
    }
  };

  return (
    <Box display='flex' flexDirection='column' alignItems='center' width='100%'>
      <Button onClick={handleLoad} sx={{ mt: 2 }} variant='contained'>
        Load Data
      </Button>
      <Button onClick={() => setLoadedData(null)} sx={{ mt: 1 }} variant='contained' color='secondary'>
        Clear Data
      </Button>

      {loadedData && Array.isArray(loadedData) && loadedData.length > 0 && (
        <TableContainer component={Paper} sx={{ mt: 3, width: '90%' }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell><strong>ID</strong></TableCell>
                <TableCell><strong>Name</strong></TableCell>
                <TableCell><strong>Email</strong></TableCell>
                <TableCell><strong>Created At</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {loadedData.map((item) => (
                <TableRow key={item.id}>
                  <TableCell>{item.id}</TableCell>
                  <TableCell>{item.name}</TableCell>
                  <TableCell>{item.email}</TableCell>
                  <TableCell>{item.extra_params?.createdate}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
};
