import { useState } from 'react';
import {
    Box,
    TextField,
    Button,
} from '@mui/material';
import axios from 'axios';

const endpointMapping = {
    'Notion': 'notion',
    'Airtable': 'airtable',
    'Hubspot': 'hubspot'   // ✅ Add Hubspot endpoint
};

export const DataForm = ({ integrationType, credentials }) => {
    const [loadedData, setLoadedData] = useState(null);
    const endpoint = endpointMapping[integrationType];

    const handleLoad = async () => {
        try {
            const formData = new FormData();
            formData.append('credentials', credentials);  // not JSON.stringify

            let url = `http://localhost:8000/integrations/${endpoint}`;
            url += (integrationType === 'Hubspot') ? `/get_hubspot_items` : `/load`;

            const response = await axios.post(url, formData);
            const data = response.data;
            setLoadedData(data);
        } catch (e) {
            console.error(e);
            alert(e?.response?.data?.detail || "Error loading data");
        }
    };

    return (
        <Box display='flex' justifyContent='center' alignItems='center' flexDirection='column' width='100%'>
            <Box display='flex' flexDirection='column' width='100%'>
                <TextField
                    label="Loaded Data"
                    value={loadedData ? JSON.stringify(loadedData, null, 2) : ''}
                    multiline
                    rows={10}
                    sx={{ mt: 2 }}
                    InputLabelProps={{ shrink: true }}
                    disabled
                />
                <Button onClick={handleLoad} sx={{ mt: 2 }} variant='contained'>
                    Load Data
                </Button>
                <Button onClick={() => setLoadedData(null)} sx={{ mt: 1 }} variant='outlined'>
                    Clear Data
                </Button>
            </Box>
        </Box>
    );
};
