import { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Typography,
} from '@mui/material';
import axios from 'axios';

const endpointMapping = {
  Notion: 'notion',
  Airtable: 'airtable',
  Hubspot: 'hubspot', // Add this!
};

export const DataForm = ({ integrationType, credentials }) => {
  const [loadedData, setLoadedData] = useState([]);
  const [error, setError] = useState(null);

  const endpoint = endpointMapping[integrationType];

  const handleLoad = async () => {
    try {
      setError(null);
      const formData = new FormData();
      formData.append('credentials', typeof credentials === 'string' ? credentials : JSON.stringify(credentials));
      const response = await axios.post(`http://localhost:8000/integrations/${endpoint}/load`, formData);
      setLoadedData(response.data || []);
    } catch (e) {
      console.error(e);
      setError(e?.response?.data?.detail || 'Error loading data');
    }
  };

  const handleClear = () => {
    setLoadedData([]);
    setError(null);
  };

  return (
    <Box sx={{ width: '100%', mt: 4 }}>
      <Typography variant="h6" gutterBottom>
        Loaded Data
      </Typography>

      <Box sx={{ mb: 2 }}>
        <Button variant="contained" onClick={handleLoad} sx={{ mr: 1 }}>
          Load Data
        </Button>
        <Button variant="outlined" onClick={handleClear}>
          Clear Data
        </Button>
      </Box>

      {error && <Typography color="error">{error}</Typography>}

      {loadedData.length > 0 && (
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Name</TableCell>
              <TableCell>Email</TableCell>
              <TableCell>Created At</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {loadedData.map((item) => (
              <TableRow key={item.id}>
                <TableCell>{item.id}</TableCell>
                <TableCell>{item.name}</TableCell>
                <TableCell>{item.email}</TableCell>
                <TableCell>{item.extra_params?.createdate || '—'}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      )}
    </Box>
  );
};
